import { useState } from "react";

const incrementar= {
  'background':'green'
}
const decrementar= {
  'background':'salmon'
}
const reset= {
  'background':'blue',
  'color':'white'
}


export const Boton = () => {
  const [nombre, setName] = useState(0);
  return (

    <div>      
      <span>Contador: {nombre}</span>

      <button style={incrementar} onClick={()=>{setName(nombre+1)}}>
        Incrementar
      </button>

      <button style={decrementar} onClick={()=>{setName(nombre-1)}}>
        Decrementar
      </button>

      <button style={reset} onClick={()=>{setName(0)}}>
        Reset
      </button>

    </div>
  );
};
