import { useState } from "react";

export const Boton = () => {
  const [nombre, setName] = useState(0);
  return (

    <div>      
      <span>Contador: {nombre}</span>

      <button onClick={()=>{setName(nombre+1)}}>
        Incrementar
      </button>

      <button onClick={()=>{setName(nombre-1)}}>
        Decrementar
      </button>

      <button onClick={()=>{setName(0)}}>
        Reset
      </button>

    </div>
  );
};
